console.log('üì• Processing webhook data...');
const data = $json.body || $json;
const posts = data.posts || [];
console.log(`üìà Received: ${posts.length} posts`);
return {
  posts: posts,
  channels_metadata: data.channels_metadata || {},
  collection_stats: data.collection_stats || {},
  timestamp: new Date().toISOString(),
  has_posts: posts.length > 0
};

console.log('üìä Grouping posts by channels...');
const data = $json;
const posts = data.posts || [];
const channelsMetadata = data.channels_metadata || {};
const grouped = {};

posts.forEach(post => {
    const key = post.channel_username || `channel_${post.channel_id}`;
    if (!grouped[key]) {
        grouped[key] = {
            channel_id: post.channel_id,
            channel_username: post.channel_username,
            channel_title: post.channel_title,
            posts: [],
            categories: channelsMetadata[post.channel_username]?.categories || []
        };
    }
    grouped[key].posts.push(post);
});

return {
    timestamp: data.timestamp,
    stats: data.collection_stats,
    grouped_posts: grouped,
    channels_metadata: channelsMetadata
};

console.log('ü§ñ Preparing data for AI with BATCHING...');
const groupedPostsData = $json;
const groupedPosts = groupedPostsData.grouped_posts || {};
const channelsMetadata = groupedPostsData.channels_metadata || {};

// –°–æ–±–∏—Ä–∞–µ–º –∞–∫—Ç–∏–≤–Ω—ã–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
const channelCategoryMap = {};
const categoryDescriptions = {};
const categoryAIPrompts = {};
let allActiveCategories = new Set();

Object.keys(groupedPosts).forEach(channelKey => {
    const channelData = groupedPosts[channelKey];
    const channelUsername = channelData.channel_username;
    const categories = channelData.categories || [];
    const activeCategories = categories.filter(cat => cat.is_active);

    channelCategoryMap[channelUsername] = activeCategories;

    activeCategories.forEach(category => {
        allActiveCategories.add(category.name);
        if (category.description) {
            categoryDescriptions[category.name] = category.description;
        }
        if (category.ai_prompt) {
            categoryAIPrompts[category.name] = category.ai_prompt;
        }
    });
});

const categories = Array.from(allActiveCategories);

// –ü–æ–¥–≥–æ—Ç–∞–≤–ª–∏–≤–∞–µ–º –ø–æ—Å—Ç—ã –¥–ª—è AI –∞–Ω–∞–ª–∏–∑–∞
const postsForAI = [];
Object.keys(groupedPosts).forEach(channelKey => {
    const channelData = groupedPosts[channelKey];
    channelData.posts.forEach(post => {
        if (post.text && post.text.length > 50) {
            postsForAI.push({
                id: post.id,
                text: post.text.substring(0, 1000),
                channel: channelData.channel_title,
                channel_username: channelData.channel_username,
                views: post.views || 0,
                date: post.date,
                url: post.url
            });
        }
    });
});

console.log(`üìù Total posts for AI: ${postsForAI.length} (will be batched by 30)`);
console.log(`üìã Sample post IDs: ${postsForAI.slice(0, 5).map(p => p.id).join(', ')}`);

// –§–æ—Ä–º–∏—Ä—É–µ–º –ø—Ä–æ–º–ø—Ç
const topicsDescription = categories.map(cat => {
    const description = categoryDescriptions[cat] || cat;
    return `${cat} (${description})`;
}).join(', ');

let dynamicPrompt = `–û—Ç—Ñ–∏–ª—å—Ç—Ä—É–π –ø–æ—Å—Ç—ã –ø–æ —Ç–µ–º–∞–º: ${topicsDescription}.\n\n–ü—Ä–∞–≤–∏–ª–æ: summary = \"NULL\" –µ—Å–ª–∏ —Ç–µ–∫—Å—Ç –ø–æ—Å—Ç–∞ –ù–ï –∏–º–µ–µ—Ç –æ—Ç–Ω–æ—à–µ–Ω–∏—è –ù–ò –ö –û–î–ù–û–ô –∏–∑ –ø–µ—Ä–µ—á–∏—Å–ª–µ–Ω–Ω—ã—Ö —Ç–µ–º.\n\n–í–æ–∑–≤—Ä–∞—â–∞–π JSON: {\"results\": [{\"id\": \"post_id\", \"summary\": \"—Ä–µ–∑—é–º–µ –∏–ª–∏ NULL\", \"importance\": 8, \"urgency\": 6, \"significance\": 7, \"category\": \"–¢–æ—á–Ω–æ–µ –Ω–∞–∑–≤–∞–Ω–∏–µ —Ç–µ–º—ã\"}]}\n\n–í–ê–ñ–ù–û: category –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å –æ–¥–Ω–∏–º –∏–∑: ${categories.join(', ')} –∏–ª–∏ \"NULL\"\n\n–ê–Ω–∞–ª–∏–∑–∏—Ä—É–π –ø–æ –°–ú–´–°–õ–£.`;

return {
    timestamp: groupedPostsData.timestamp,
    stats: groupedPostsData.stats,
    grouped_posts: groupedPosts,
    posts_for_ai: postsForAI,
    total_posts_for_ai: postsForAI.length,
    dynamic_prompt: dynamicPrompt,
    categories: categories,
    channel_category_map: channelCategoryMap,
    batch_processing: true,
    version: 'v7.3_with_batches_debug'
};

const batchData = $json;
const prepareData = $('Prepare for AI').all()[0]?.json;
const currentBatch = batchData.posts_for_ai || [];
const batchIndex = $runIndex + 1;
const totalBatches = Math.ceil(prepareData.total_posts_for_ai / 30);

console.log(`üîÑ Processing batch ${batchIndex}/${totalBatches} with ${currentBatch.length} posts`);
console.log(`üìã Batch ${batchIndex} post IDs: ${currentBatch.map(p => p.id).join(', ')}`);

return {
    dynamic_prompt: prepareData.dynamic_prompt,
    posts_for_ai: currentBatch,
    batch_index: batchIndex,
    total_batches: totalBatches,
    total_posts_in_batch: currentBatch.length
};

const openAIResponse = $json;
const batchData = $('Prepare Batch').all()[$runIndex]?.json;

console.log(`üìù Processing OpenAI response for batch ${batchData?.batch_index}`);
console.log(`üîç OpenAI Response structure:`, JSON.stringify(openAIResponse, null, 2));

let aiAnalysis = [];
if (openAIResponse.message?.content?.results) {
    aiAnalysis = openAIResponse.message.content.results;
    console.log(`‚úÖ Found results in message.content.results: ${aiAnalysis.length}`);
} else if (openAIResponse[0]?.message?.content?.results) {
    aiAnalysis = openAIResponse[0].message.content.results;
    console.log(`‚úÖ Found results in [0].message.content.results: ${aiAnalysis.length}`);
} else if (openAIResponse.message?.content && typeof openAIResponse.message.content === 'string') {
    try {
        const parsed = JSON.parse(openAIResponse.message.content);
        aiAnalysis = parsed.results || parsed;
        console.log(`‚úÖ Parsed from string content: ${aiAnalysis.length}`);
    } catch (error) {
        console.log('‚ö†Ô∏è Failed to parse string content:', error.message);
    }
} else {
    console.log('‚ùå No valid AI results found in any expected structure');
    console.log('üîç Available keys:', Object.keys(openAIResponse));
}

console.log(`‚úÖ Extracted ${aiAnalysis.length} results from batch ${batchData?.batch_index}`);
if (aiAnalysis.length > 0) {
    console.log(`üìã Sample AI result:`, JSON.stringify(aiAnalysis[0], null, 2));
}

return {
    batch_index: batchData?.batch_index || $runIndex + 1,
    batch_results: aiAnalysis,
    batch_posts_count: batchData?.total_posts_in_batch || 0,
    processed_at: new Date().toISOString(),
    debug_info: {
        original_response_keys: Object.keys(openAIResponse),
        ai_results_count: aiAnalysis.length
    }
};

console.log('üîó Merging all batch results...');
const allBatchResults = $input.all();
const prepareData = $('Prepare for AI').all()[0]?.json;

let allAIAnalysis = [];
let totalProcessedPosts = 0;

allBatchResults.forEach((batchResult, index) => {
    const batchData = batchResult.json;
    console.log(`üì¶ Batch ${batchData.batch_index}: ${batchData.batch_results.length} results`);
    allAIAnalysis = allAIAnalysis.concat(batchData.batch_results);
    totalProcessedPosts += batchData.batch_posts_count;
});

console.log(`‚úÖ Merged ${allAIAnalysis.length} AI results from ${allBatchResults.length} batches`);
console.log(`üìã Sample merged AI IDs: ${allAIAnalysis.slice(0, 5).map(item => item.id).join(', ')}`);

// –ü—Ä–∏–º–µ–Ω—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç—ã –∫ –ø–æ—Å—Ç–∞–º
const groupedPosts = prepareData.grouped_posts || {};
const channelCategoryMap = prepareData.channel_category_map || {};
const processedChannels = {};

Object.keys(groupedPosts).forEach(channelKey => {
    const channelData = groupedPosts[channelKey];
    const channelCategories = channelCategoryMap[channelData.channel_username] || [];

    console.log(`üîç Processing channel: ${channelData.channel_title} with ${channelData.posts.length} posts`);

    const processedPosts = channelData.posts.map(post => {
        const analysis = allAIAnalysis.find(item => item.id == post.id);
        const isRelevant = analysis && analysis.summary && analysis.summary !== 'NULL';

        if (analysis) {
            console.log(`‚úÖ Found AI analysis for post ${post.id}: relevant=${isRelevant}`);
        } else {
            console.log(`‚ùå No AI analysis found for post ${post.id}`);
        }

        return {
            ...post,
            ai_summary: analysis?.summary || post.text?.substring(0, 150) + '...',
            ai_importance: analysis?.importance || 0,
            ai_urgency: analysis?.urgency || 0,
            ai_significance: analysis?.significance || 0,
            post_category: analysis?.category || 'Unknown',
            is_relevant: isRelevant,
            parsing_method: 'batch_processing_v7.3_debug'
        };
    });

    const relevantPosts = processedPosts.filter(post => post.is_relevant);
    console.log(`üìä ${channelData.channel_title}: ${processedPosts.length} ‚Üí ${relevantPosts.length} relevant`);

    relevantPosts.sort((a, b) => {
        const scoreA = a.ai_importance * 3 + a.ai_urgency * 2 + a.ai_significance * 2 + Math.log(a.views || 1);
        const scoreB = b.ai_importance * 3 + b.ai_urgency * 2 + b.ai_significance * 2 + Math.log(b.views || 1);
        return scoreB - scoreA;
    });

    processedChannels[channelKey] = {
        ...channelData,
        posts: relevantPosts.slice(0, 8),
        all_processed_posts: processedPosts.length,
        relevant_posts: relevantPosts.length,
        ai_processed: true,
        channel_categories: channelCategories.map(c => c.name)
    };
});

return {
    timestamp: prepareData.timestamp,
    processed_at: new Date().toISOString(),
    stats: prepareData.stats,
    processed_channels: processedChannels,
    total_channels: Object.keys(processedChannels).length,
    ai_analysis_stats: {
        total_analyzed: allAIAnalysis.length,
        relevant_posts: Object.values(processedChannels).reduce((sum, ch) => sum + ch.relevant_posts, 0),
        batches_processed: allBatchResults.length,
        total_posts_processed: totalProcessedPosts,
        avg_importance: allAIAnalysis.reduce((sum, item) => sum + (item.importance || 0), 0) / Math.max(allAIAnalysis.length, 1),
        avg_urgency: allAIAnalysis.reduce((sum, item) => sum + (item.urgency || 0), 0) / Math.max(allAIAnalysis.length, 1),
        avg_significance: allAIAnalysis.reduce((sum, item) => sum + (item.significance || 0), 0) / Math.max(allAIAnalysis.length, 1)
    },
    batch_processing_applied: true,
    relevance_parsing_version: 'v7.3_batch_processing_debug'
};

const processedData = $json;

console.log(`üîç Prepare Digest: ${Object.keys(processedData.processed_channels).length} channels to process`);

const digest = {
  id: `digest_${Date.now()}`,
  created_at: processedData.timestamp,
  processed_at: processedData.processed_at,
  channels: [],
  total_posts: 0,
  batch_processing_applied: true,
  summary: {
    channels_processed: Object.keys(processedData.processed_channels).length,
    original_posts: processedData.stats?.total_posts || 0,
    relevant_posts: processedData.ai_analysis_stats?.relevant_posts || 0,
    avg_importance: processedData.ai_analysis_stats?.avg_importance || 0,
    avg_urgency: processedData.ai_analysis_stats?.avg_urgency || 0,
    avg_significance: processedData.ai_analysis_stats?.avg_significance || 0,
    batches_processed: processedData.ai_analysis_stats?.batches_processed || 0
  }
};

Object.keys(processedData.processed_channels).forEach(channelKey => {
  const channelData = processedData.processed_channels[channelKey];

  console.log(`üì∫ Adding channel ${channelData.channel_title}: ${channelData.posts.length} posts`);

  digest.channels.push({
    title: channelData.channel_title,
    username: channelData.channel_username,
    categories: channelData.channel_categories || [],
    posts_count: channelData.posts.length,
    posts: channelData.posts.map(post => ({
      title: (post.text || '').substring(0, 100) + '...',
      url: post.url,
      views: post.views,
      date: post.date,
      ai_importance: post.ai_importance,
      ai_urgency: post.ai_urgency,
      ai_significance: post.ai_significance,
      summary: post.ai_summary,
      post_category: post.post_category
    }))
  });

  digest.total_posts += channelData.posts.length;
});

console.log(`‚úÖ Debug digest ready: ${digest.total_posts} posts, ${digest.summary.batches_processed} batches`);
console.log(`üìã Final channels with posts: ${digest.channels.filter(ch => ch.posts_count > 0).length}`);

return digest;

const digest = $json;

const backendPayload = {
  digest_id: digest.id,
  total_posts: digest.total_posts,
  channels_processed: digest.summary.channels_processed,
  original_posts: digest.summary.original_posts,
  relevant_posts: digest.summary.relevant_posts,
  avg_importance: digest.summary.avg_importance,
  avg_urgency: digest.summary.avg_urgency,
  avg_significance: digest.summary.avg_significance,
  batch_processing_applied: digest.batch_processing_applied,
  digest_data: JSON.stringify(digest),
  processed_at: digest.processed_at
};

return {
  success: true,
  digest_id: digest.id,
  backend_payload: backendPayload,
  message: `Debug batch digest: ${digest.total_posts} posts, ${digest.summary.batches_processed} batches`,
  timestamp: new Date().toISOString()
};

const apiResponse = $json;

if (apiResponse.digest_id) {
  console.log('üéâ Debug batch digest saved successfully!');
  return {
    success: true,
    digest_id: apiResponse.digest_id,
    message: 'Debug batch digest saved!',
    timestamp: new Date().toISOString()
  };
} else {
  return {
    success: false,
    error: 'Failed to save debug batch digest',
    timestamp: new Date().toISOString()
  };
}

console.log('‚ö†Ô∏è No posts to process');
return {
  success: false,
  message: 'No posts to process',
  timestamp: new Date().toISOString()
};
