// –ü–æ–ª—É—á–µ–Ω–∏–µ –∏ –ª–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –æ—Ç userbot —Å –∏—Å–ø—Ä–∞–≤–ª–µ–Ω–∏–µ–º webhook —Å—Ç—Ä—É–∫—Ç—É—Ä—ã
const rawData = $json;
console.log('üì• Raw webhook data received:', JSON.stringify(rawData, null, 2));

// –ò—Å–ø—Ä–∞–≤–ª—è–µ–º —Å—Ç—Ä—É–∫—Ç—É—Ä—É –¥–∞–Ω–Ω—ã—Ö webhook (body vs direct)
const data = rawData.body || rawData;
console.log('üìä Processing data structure...');

if (!data) {
    console.log('‚ùå No data found in webhook');
    return { error: 'No data received', timestamp: new Date().toISOString() };
}

const posts = data.posts || [];
const channelsMetadata = data.channels_metadata || {};
const collectionStats = data.collection_stats || {};

console.log(`üìà Received: ${posts.length} posts from ${Object.keys(channelsMetadata).length} channels`);
console.log('üìã Collection stats:', collectionStats);

if (posts.length === 0) {
    console.log('‚ö†Ô∏è No posts to process');
    return {
        posts: [],
        channels_metadata: channelsMetadata,
        collection_stats: collectionStats,
        timestamp: new Date().toISOString(),
        has_posts: false
    };
}

console.log('‚úÖ Posts processed successfully');
