};

// –ì—Ä—É–ø–ø–∏—Ä–æ–≤–∫–∞ –ø–æ—Å—Ç–æ–≤ –ø–æ –∫–∞–Ω–∞–ª–∞–º —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º –º–µ—Ç–∞–¥–∞–Ω–Ω—ã—Ö
const data = $json;
const posts = data.posts || [];
const channelsMetadata = data.channels_metadata || {};

console.log('üìä Grouping posts by channels...');
console.log('üìã Available channels metadata:', Object.keys(channelsMetadata));

const grouped = {};

posts.forEach(post => {
    const key = post.channel_username || `channel_${post.channel_id}`;

    if (!grouped[key]) {
        grouped[key] = {
            channel_id: post.channel_id,
            channel_username: post.channel_username,
            channel_title: post.channel_title,
            posts: [],
            categories: channelsMetadata[post.channel_username]?.categories || []
        };
    }

    grouped[key].posts.push(post);
});

const result = {
    timestamp: data.timestamp,
    stats: data.collection_stats,
    grouped_posts: grouped,
    channels_metadata: channelsMetadata
};

console.log(`üìà Grouped into ${Object.keys(grouped).length} channels`);
Object.keys(grouped).forEach(key => {
    const channel = grouped[key];
    console.log(`  üì∫ ${channel.channel_title}: ${channel.posts.length} posts, ${channel.categories.length} categories`);
});

return result;

// –ü–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö –¥–ª—è AI —Å Binary Relevance + –ú–µ—Ç—Ä–∏–∫–∏ –ø–æ–¥—Ö–æ–¥–æ–º v6.0
console.log('ü§ñ Preparing data for OpenAI with Binary Relevance + Metrics v6.0...');

const groupedPostsData = $json;
const groupedPosts = groupedPostsData.grouped_posts || {};
const channelsMetadata = groupedPostsData.channels_metadata || {};

console.log(`üìã Found grouped posts for ${Object.keys(groupedPosts).length} channels`);
console.log('üìã Channels metadata available:', Object.keys(channelsMetadata));

// –°–æ–±–∏—Ä–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –ø–æ Many-to-Many —Å–≤—è–∑—è–º –¥–ª—è –∫–∞–∂–¥–æ–≥–æ –∫–∞–Ω–∞–ª–∞
const channelCategoryMap = {};
const categoryPrompts = {};
let allActiveCategories = new Set();

Object.keys(groupedPosts).forEach(channelKey => {
    const channelData = groupedPosts[channelKey];
    const channelUsername = channelData.channel_username;

    console.log(`üìã –ö–∞–Ω–∞–ª: ${channelData.channel_title} (${channelUsername})`);

    // –ò—Å–ø–æ–ª—å–∑—É–µ–º –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –ø—Ä—è–º–æ –∏–∑ grouped –¥–∞–Ω–Ω—ã—Ö –∏–ª–∏ –º–µ—Ç–∞–¥–∞–Ω–Ω—ã—Ö
    const categories = channelData.categories || channelsMetadata[channelUsername]?.categories || [];
    const activeCategories = categories.filter(cat => cat.is_active);

    channelCategoryMap[channelUsername] = activeCategories;

    activeCategories.forEach(category => {
        allActiveCategories.add(category.name);
        // –°–æ—Ö—Ä–∞–Ω—è–µ–º –∏–Ω–¥–∏–≤–∏–¥—É–∞–ª—å–Ω—ã–µ AI –ø—Ä–æ–º–ø—Ç—ã –¥–ª—è –∫–∞–∂–¥–æ–π –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
        if (category.ai_prompt) {
            categoryPrompts[category.name] = category.ai_prompt;
        }
        console.log(`  üè∑Ô∏è –ê–∫—Ç–∏–≤–Ω–∞—è –∫–∞—Ç–µ–≥–æ—Ä–∏—è: ${category.name}`);
        console.log(`    üìù AI prompt: ${category.ai_prompt?.substring(0, 80)}...`);
    });

    if (activeCategories.length === 0) {
        console.log(`  ‚ö†Ô∏è –ù–µ—Ç –∞–∫—Ç–∏–≤–Ω—ã—Ö –∫–∞—Ç–µ–≥–æ—Ä–∏–π –¥–ª—è ${channelUsername}`);
    }
});

// –§–æ—Ä–º–∏—Ä—É–µ–º —Å–ø–∏—Å–æ–∫ –∫–∞—Ç–µ–≥–æ—Ä–∏–π –¥–ª—è OpenAI
const categories = Array.from(allActiveCategories);

console.log(`‚úÖ Binary Relevance + Metrics v6.0: –ù–∞–π–¥–µ–Ω–æ ${categories.length} —É–Ω–∏–∫–∞–ª—å–Ω—ã—Ö –∞–∫—Ç–∏–≤–Ω—ã—Ö –∫–∞—Ç–µ–≥–æ—Ä–∏–π:`, categories);
console.log('üîó –°–≤—è–∑–∏ –∫–∞–Ω–∞–ª–æ–≤ —Å –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º–∏:');
Object.keys(channelCategoryMap).forEach(channel => {
    const cats = channelCategoryMap[channel].map(c => c.name).join(', ');
    console.log(`  ${channel} ‚Üí [${cats}]`);
});

// –ü–æ–¥–≥–æ—Ç–∞–≤–ª–∏–≤–∞–µ–º –ø–æ—Å—Ç—ã –¥–ª—è AI –∞–Ω–∞–ª–∏–∑–∞
const postsForAI = [];

Object.keys(groupedPosts).forEach(channelKey => {
    const channelData = groupedPosts[channelKey];

    channelData.posts.forEach(post => {
        if (post.text && post.text.length > 50) {
            postsForAI.push({
                id: post.id,
                text: post.text.substring(0, 1000),
                channel: channelData.channel_title,
                channel_username: channelData.channel_username,
                views: post.views || 0,
                date: post.date,
                url: post.url,
                // –î–æ–±–∞–≤–ª—è–µ–º —Å–≤—è–∑–∞–Ω–Ω—ã–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –∫–∞–Ω–∞–ª–∞
                channel_categories: channelCategoryMap[channelData.channel_username]?.map(c => c.name) || []
            });
        }
    });
});

console.log(`üìù Prepared ${postsForAI.length} posts for AI analysis`);

// –§–æ—Ä–º–∏—Ä—É–µ–º —Å–ø–∏—Å–æ–∫ —Ç–µ–º –¥–ª—è Binary Relevance –∞–Ω–∞–ª–∏–∑–∞ (—á–µ—Ä–µ–∑ –∑–∞–ø—è—Ç—ã–µ)
const topicsDescription = categories.map(cat => {
    const prompt = categoryPrompts[cat] || cat;
    return `${cat} (${prompt})`;
}).join(', ');

// –ù–æ–≤—ã–π –ø—Ä–æ–º–ø—Ç —Å Binary Relevance + –ú–µ—Ç—Ä–∏–∫–∏ v6.0
const dynamicPrompt = `–û—Ç—Ñ–∏–ª—å—Ç—Ä—É–π –ø–æ—Å—Ç—ã –ø–æ —Ç–µ–º–µ: ${topicsDescription}.\n\n–ü—Ä–∞–≤–∏–ª–æ: summary = \"NULL\" –µ—Å–ª–∏ —Ç–µ–∫—Å—Ç –ø–æ—Å—Ç–∞ –ù–ï –∏–º–µ–µ—Ç –æ—Ç–Ω–æ—à–µ–Ω–∏—è –ù–ò –ö –û–î–ù–û–ô –∏–∑ –ø–µ—Ä–µ—á–∏—Å–ª–µ–Ω–Ω—ã—Ö —Ç–µ–º.\n\n–î–ª—è –∫–∞–∂–¥–æ–≥–æ –ø–æ—Å—Ç–∞:\n\n–ï–°–õ–ò –ø–æ—Å—Ç –∏–º–µ–µ—Ç –æ—Ç–Ω–æ—à–µ–Ω–∏–µ –∫ —Ç–µ–º–µ - –¥–∞–π –∫—Ä–∞—Ç–∫–æ–µ —Ä–µ–∑—é–º–µ + –º–µ—Ç—Ä–∏–∫–∏ 1-10.\n–ï–°–õ–ò –ø–æ—Å—Ç –ù–ï –∏–º–µ–µ—Ç –æ—Ç–Ω–æ—à–µ–Ω–∏—è –∫ —Ç–µ–º–∞–º - –Ω–∞–ø–∏—à–∏ \"NULL\" + –º–µ—Ç—Ä–∏–∫–∏ 0.\n\n–í–æ–∑–≤—Ä–∞—â–∞–π JSON:\n{\"results\": [{\"id\": \"post_id\", \"summary\": \"—Ä–µ–∑—é–º–µ –∏–ª–∏ NULL\", \"importance\": 8, \"urgency\": 6, \"significance\": 7}]}\n\n–ê–Ω–∞–ª–∏–∑–∏—Ä—É–π –ø–æ –°–ú–´–°–õ–£, –∞ –Ω–µ —Ç–æ–ª—å–∫–æ –ø–æ –∫–ª—é—á–µ–≤—ã–º —Å–ª–æ–≤–∞–º. –£—á–∏—Ç—ã–≤–∞–π –∫–æ–Ω—Ç–µ–∫—Å—Ç –∏ —Å–µ–º–∞–Ω—Ç–∏–∫—É.`;

console.log('üîÆ Generated Binary Relevance + Metrics prompt v6.1 (semantic analysis)');
console.log('üìä Categories for analysis:', categories);
console.log('üìä Categories with AI prompts:', Object.keys(categoryPrompts).length);

// FALLBACK —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –≤–æ–æ–±—â–µ –Ω–µ—Ç –∫–∞—Ç–µ–≥–æ—Ä–∏–π
if (categories.length === 0) {
    console.log('‚ùå –ö–†–ò–¢–ò–ß–ï–°–ö–ê–Ø –û–®–ò–ë–ö–ê: –ù–µ—Ç –∞–∫—Ç–∏–≤–Ω—ã—Ö –∫–∞—Ç–µ–≥–æ—Ä–∏–π, –∏—Å–ø–æ–ª—å–∑—É–µ–º fallback');
    const fallbackPrompt = `–ê–Ω–∞–ª–∏–∑–∏—Ä—É–π –ø–æ—Å—Ç—ã –∏ –æ–ø—Ä–µ–¥–µ–ª—è–π –∏—Ö —Ä–µ–ª–µ–≤–∞–Ω—Ç–Ω–æ—Å—Ç—å –æ–±—â–∏–º –Ω–æ–≤–æ—Å—Ç–Ω—ã–º —Ç–µ–º–∞–º. –î–ª—è —Ä–µ–ª–µ–≤–∞–Ω—Ç–Ω—ã—Ö –ø–æ—Å—Ç–æ–≤ –¥–∞–≤–∞–π –∫—Ä–∞—Ç–∫–æ–µ —Ä–µ–∑—é–º–µ –∏ –º–µ—Ç—Ä–∏–∫–∏ –≤–∞–∂–Ω–æ—Å—Ç–∏/—Å—Ä–æ—á–Ω–æ—Å—Ç–∏/–∑–Ω–∞—á–∏–º–æ—Å—Ç–∏, –¥–ª—è –Ω–µ—Ä–µ–ª–µ–≤–∞–Ω—Ç–Ω—ã—Ö - \"NULL\".`;

    return {
        timestamp: groupedPostsData.timestamp,
        stats: groupedPostsData.stats,
        grouped_posts: groupedPosts,
        posts_for_ai: postsForAI,
        total_posts_for_ai: postsForAI.length,
        dynamic_prompt: fallbackPrompt,
        categories: ['–û–±—â–∏–µ –Ω–æ–≤–æ—Å—Ç–∏'],
        channel_category_map: {},
        error: 'No active categories found'
    };
}

return {
    timestamp: groupedPostsData.timestamp,
    stats: groupedPostsData.stats,
    grouped_posts: groupedPosts,
    posts_for_ai: postsForAI,
    total_posts_for_ai: postsForAI.length,
    dynamic_prompt: dynamicPrompt,
    categories: categories,
    channel_category_map: channelCategoryMap,
    category_prompts: categoryPrompts,
    binary_relevance: true,
    with_metrics: true
};

// –§–∏–Ω–∞–ª—å–Ω–∞—è –ø–æ–¥–≥–æ—Ç–æ–≤–∫–∞ –¥–∞–π–¥–∂–µ—Å—Ç–∞ —Å Binary Relevance + Metrics –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–µ–π
const processedChannels = $json.processed_channels || {};

console.log('üìÑ Preparing final digest with Binary Relevance + Metrics...');

const digest = {
  id: `digest_${Date.now()}`,
  created_at: $json.timestamp,
  processed_at: $json.processed_at,
  channels: [],
  total_posts: 0,
  binary_relevance_applied: true,
  with_metrics: true,
  summary: {
    channels_processed: Object.keys(processedChannels).length,
    original_posts: $json.stats?.total_posts || 0,
    relevant_posts: $json.ai_analysis_stats?.relevant_posts || 0,
    avg_importance: $json.ai_analysis_stats?.avg_importance || 0,
    avg_urgency: $json.ai_analysis_stats?.avg_urgency || 0,
    avg_significance: $json.ai_analysis_stats?.avg_significance || 0
  }
};

Object.keys(processedChannels).forEach(channelKey => {
  const channelData = processedChannels[channelKey];

  digest.channels.push({
    title: channelData.channel_title,
    username: channelData.channel_username,
    categories: channelData.channel_categories || [],
    posts_count: channelData.posts.length,
    relevant_posts: channelData.relevant_posts || 0,
    posts: channelData.posts.map(post => ({
      title: (post.text || '').substring(0, 100) + '...',
      url: post.url,
      views: post.views,
      date: post.date,
      ai_importance: post.ai_importance,
      ai_urgency: post.ai_urgency,
      ai_significance: post.ai_significance,
      summary: post.ai_summary
    }))
  });

  digest.total_posts += channelData.posts.length;
});

console.log(`‚úÖ Digest ready with Binary Relevance + Metrics: ${digest.total_posts} posts from ${digest.channels.length} channels`);
console.log(`üìä Relevant posts: ${digest.summary.relevant_posts}`);
console.log(`üìà Avg metrics - Importance: ${digest.summary.avg_importance.toFixed(1)}, Urgency: ${digest.summary.avg_urgency.toFixed(1)}, Significance: ${digest.summary.avg_significance.toFixed(1)}`);

return digest;

// –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –¥–∞–π–¥–∂–µ—Å—Ç–∞ –≤ Backend API
const digest = $json;

console.log('üíæ Saving digest to Backend API...');
console.log('Digest ID:', digest.id);
console.log('Total posts:', digest.total_posts);
console.log('Channels:', digest.channels.length);
console.log('Binary Relevance applied:', digest.binary_relevance_applied);
console.log('With Metrics:', digest.with_metrics);
console.log('Relevant posts:', digest.summary.relevant_posts);

// –ü–æ–¥–≥–æ—Ç–∞–≤–ª–∏–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è Backend API
const backendPayload = {
  digest_id: digest.id,
  total_posts: digest.total_posts,
  channels_processed: digest.summary.channels_processed,
  original_posts: digest.summary.original_posts,
  relevant_posts: digest.summary.relevant_posts,
  avg_importance: digest.summary.avg_importance,
  avg_urgency: digest.summary.avg_urgency,
  avg_significance: digest.summary.avg_significance,
  binary_relevance_applied: digest.binary_relevance_applied,
  with_metrics: digest.with_metrics,
  digest_data: JSON.stringify(digest), // –ü–æ–ª–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –¥–∞–π–¥–∂–µ—Å—Ç–∞ –∫–∞–∫ JSON —Å—Ç—Ä–æ–∫–∞
  processed_at: digest.processed_at
};

console.log('üì§ Payload for Backend API:', JSON.stringify(backendPayload, null, 2));

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º payload –¥–ª—è HTTP Request node
return {
  success: true,
  digest_id: digest.id,
  backend_payload: backendPayload,
  message: `Binary Relevance + Metrics digest prepared for Backend API: ${digest.total_posts} posts, ${digest.summary.relevant_posts} relevant`,
  timestamp: new Date().toISOString(),
  metrics_stats: {
    avg_importance: digest.summary.avg_importance,
    avg_urgency: digest.summary.avg_urgency,
    avg_significance: digest.summary.avg_significance,
    channels_with_categories: digest.channels.filter(ch => ch.categories.length > 0).length
  }
};

// –§–∏–Ω–∞–ª—å–Ω–∞—è –æ–±—Ä–∞–±–æ—Ç–∫–∞ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è –≤ Backend API
const apiResponse = $json;
const preparePayloadData = $('Prepare Backend Payload').all()[0]?.json;

console.log('‚úÖ Backend API response:', JSON.stringify(apiResponse, null, 2));

if (apiResponse.digest_id) {
  console.log('üéâ Digest successfully saved to Backend API!');
  console.log('üìä Digest ID:', apiResponse.digest_id);
  console.log('üìà Total posts:', apiResponse.total_posts);
  console.log('üìä Relevant posts:', apiResponse.relevant_posts);

  return {
    success: true,
    digest_id: apiResponse.digest_id,
    backend_id: apiResponse.id,
    message: `Digest saved to Backend API successfully!`,
    timestamp: new Date().toISOString(),
    api_response: apiResponse,
    metrics_stats: preparePayloadData?.metrics_stats || {}
  };
} else {
  console.log('‚ùå Failed to save digest to Backend API');

  return {
    success: false,
    error: 'Failed to save to Backend API',
    api_response: apiResponse,
    timestamp: new Date().toISOString()
  };
}

// –û–±—Ä–∞–±–æ—Ç–∫–∞ —Å–ª—É—á–∞—è –∫–æ–≥–¥–∞ –Ω–µ—Ç –ø–æ—Å—Ç–æ–≤
console.log('‚ö†Ô∏è No posts received from userbot');

return {
  success: false,
  message: 'No posts to process',
  timestamp: new Date().toISOString()
};

// –û–±—Ä–∞–±–æ—Ç–∫–∞ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤ –æ—Ç OpenAI —Å Binary Relevance + –ú–µ—Ç—Ä–∏–∫–∏ –ø–æ–¥—Ö–æ–¥–æ–º v6.0
console.log('ü§ñ Processing OpenAI response with Binary Relevance + Metrics v6.0...');

try {
    // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –æ—Ç –ø—Ä–µ–¥—ã–¥—É—â–∏—Ö —É–∑–ª–æ–≤
    const prepareForAIData = $('Prepare for AI').all()[0]?.json;
    if (!prepareForAIData) {
        throw new Error('Could not find data from Prepare for AI node');
    }

    const groupedPosts = prepareForAIData.grouped_posts || {};
    const channelCategoryMap = prepareForAIData.channel_category_map || {};
    const originalStats = prepareForAIData.stats || {};
    const originalTimestamp = prepareForAIData.timestamp || '';

    console.log('‚úÖ Found data from Prepare for AI node');
    console.log('üìä Channel-Category mapping:', Object.keys(channelCategoryMap));

    // –ò–∑–≤–ª–µ–∫–∞–µ–º AI –æ—Ç–≤–µ—Ç (—É–ª—É—á—à–µ–Ω–Ω–æ–µ –ª–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –∏ –ø–∞—Ä—Å–∏–Ω–≥)
    let aiAnalysis = [];

    console.log('üîç RAW OpenAI response structure:', JSON.stringify($json, null, 2));

    if ($json.message?.content?.results) {
        aiAnalysis = $json.message.content.results;
        console.log('‚úÖ Found AI response in $json.message.content.results (direct format)', aiAnalysis.length, 'items');
    } else if ($json[0]?.message?.content?.results) {
        aiAnalysis = $json[0].message.content.results;
        console.log('‚úÖ Found AI response in $json[0].message.content.results (array format)', aiAnalysis.length, 'items');
    } else if ($json[0]?.message?.content?.posts) {
        aiAnalysis = $json[0].message.content.posts;
        console.log('‚úÖ Found AI response in $json[0].message.content.posts (array format)', aiAnalysis.length, 'items');
    } else if ($json[0]?.message?.content && typeof $json[0].message.content === 'object') {
        // –í–æ–∑–º–æ–∂–Ω–æ content —ç—Ç–æ –æ–±—ä–µ–∫—Ç –Ω–∞–ø—Ä—è–º—É—é, –∞ –Ω–µ —Å—Ç—Ä–æ–∫–∞
        if ($json[0].message.content.results) {
            aiAnalysis = $json[0].message.content.results;
            console.log('‚úÖ Found AI response in $json[0].message.content.results (direct object)', aiAnalysis.length, 'items');
        } else {
            console.log('‚ö†Ô∏è Content is object but no results field found');
        }
    } else if ($json.message?.content && typeof $json.message.content === 'string') {
        try {
            const parsed = JSON.parse($json.message.content);
            aiAnalysis = parsed.results || parsed;
            console.log('‚úÖ Found AI response in message.content (string format)', aiAnalysis.length, 'items');
        } catch (error) {
            console.log('‚ö†Ô∏è Failed to parse message.content as JSON:', error.message);
        }
    } else if (Array.isArray($json)) {
        aiAnalysis = $json;
        console.log('‚úÖ Found AI response as direct array', aiAnalysis.length, 'items');
    } else {
        console.log('‚ùå Could not find AI response in expected fields');
        console.log('üîç Available $json structure keys:', Object.keys($json));
        if ($json[0]) {
            console.log('üîç $json[0] structure keys:', Object.keys($json[0]));
            if ($json[0].message) {
                console.log('üîç $json[0].message structure keys:', Object.keys($json[0].message));
            }
        }
    }

    console.log(`üìù AI Analysis extracted: ${aiAnalysis.length} items`);

    // –ü—Ä–∏–º–µ–Ω—è–µ–º AI –∞–Ω–∞–ª–∏–∑ –∫ –ø–æ—Å—Ç–∞–º —Å Binary Relevance –ø–æ–¥—Ö–æ–¥–æ–º
    const processedChannels = {};

    Object.keys(groupedPosts).forEach(channelKey => {
        const channelData = groupedPosts[channelKey];
        const channelCategories = channelCategoryMap[channelData.channel_username] || [];

        console.log(`üîÑ Processing channel: ${channelData.channel_title}`);
        console.log(`  üè∑Ô∏è Channel categories: ${channelCategories.map(c => c.name).join(', ')}`);

        const processedPosts = channelData.posts.map(post => {
            // –ò—â–µ–º AI –∞–Ω–∞–ª–∏–∑ –¥–ª—è —ç—Ç–æ–≥–æ –ø–æ—Å—Ç–∞
            const analysis = aiAnalysis.find(item => 
                item.id == post.id || 
                (item.text && post.text && item.text.includes(post.text.substring(0, 50)))
            );

            // Binary Relevance v6.0: –ø—Ä–æ—Å—Ç–æ –ø—Ä–æ–≤–µ—Ä—è–µ–º summary !== \"NULL\"
            const isRelevant = analysis && analysis.summary && analysis.summary !== 'NULL';

            const result = {
                ...post,
                ai_summary: analysis?.summary || post.text?.substring(0, 150) + '...',
                ai_importance: analysis?.importance || 0,
                ai_urgency: analysis?.urgency || 0,
                ai_significance: analysis?.significance || 0,
                processed_by_ai: !!analysis,
                channel_categories: channelCategories.map(c => c.name),
                // Binary Relevance: —Ä–µ–ª–µ–≤–∞–Ω—Ç–µ–Ω –µ—Å–ª–∏ summary –Ω–µ NULL
                is_relevant: isRelevant,
                parsing_method: 'binary_relevance_v6.0'
            };

            console.log(`  üìù Post ${post.id}: relevant=${isRelevant}, importance=${result.ai_importance}, urgency=${result.ai_urgency}, significance=${result.ai_significance}`);
            return result;
        });

        // –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–æ–ª—å–∫–æ –ø–æ —Ä–µ–ª–µ–≤–∞–Ω—Ç–Ω–æ—Å—Ç–∏ (summary !== NULL)
        const relevantPosts = processedPosts.filter(post => {
            if (!post.is_relevant) {
                console.log(`  ‚ö†Ô∏è –ü–æ—Å—Ç ${post.id} –æ—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞–Ω: –Ω–µ —Ä–µ–ª–µ–≤–∞–Ω—Ç–µ–Ω (summary=NULL)`);
                return false;
            }
            return true;
        });

        // –£–º–Ω–∞—è —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –ø–æ –º–µ—Ç—Ä–∏–∫–∞–º + views
        relevantPosts.sort((a, b) => {
            const scoreA = a.ai_importance * 3 + a.ai_urgency * 2 + a.ai_significance * 2 + Math.log(a.views || 1);
            const scoreB = b.ai_importance * 3 + b.ai_urgency * 2 + b.ai_significance * 2 + Math.log(b.views || 1);
            return scoreB - scoreA;
        });

        processedChannels[channelKey] = {
            ...channelData,
            posts: relevantPosts.slice(0, 8), // –¢–æ–ø 8
            all_processed_posts: processedPosts.length,
            relevant_posts: relevantPosts.length,
            ai_processed: true,
            channel_categories: channelCategories.map(c => c.name)
        };

        console.log(`üìä ${channelData.channel_title}: ${processedPosts.length} total ‚Üí ${relevantPosts.length} relevant ‚Üí ${Math.min(8, relevantPosts.length)} final`);
    });

    const result = {
        timestamp: originalTimestamp || new Date().toISOString(),
        processed_at: new Date().toISOString(),
        stats: originalStats,
        processed_channels: processedChannels,
        total_channels: Object.keys(processedChannels).length,
        ai_analysis_stats: {
            total_analyzed: aiAnalysis.length,
            avg_importance: aiAnalysis.reduce((sum, item) => sum + (item.importance || 0), 0) / Math.max(aiAnalysis.length, 1),
            avg_urgency: aiAnalysis.reduce((sum, item) => sum + (item.urgency || 0), 0) / Math.max(aiAnalysis.length, 1),
            avg_significance: aiAnalysis.reduce((sum, item) => sum + (item.significance || 0), 0) / Math.max(aiAnalysis.length, 1),
            relevant_posts: Object.values(processedChannels).reduce((sum, ch) => sum + ch.relevant_posts, 0)
        },
        many_to_many_applied: true,
        relevance_parsing_version: 'v6.0_binary_relevance_with_metrics'
    };

    console.log('üîç FINAL RESULT with Binary Relevance + Metrics v6.0:', JSON.stringify(result, null, 2));
    return result;

} catch (error) {
    console.log('‚ùå Error in Process AI Results:', error.message);
    return {
        error: 'Failed to process AI results: ' + error.message,
        timestamp: new Date().toISOString(),
        processed_channels: {},
        total_channels: 0
    };
}
